command:example.dev:code:
$allMethods = ls -recurse | where {  -match 'method' };
          $methodData = $allMethods | select-object Name,@{'Name'='ParameterCount';'Expression'={[int]@(.Parameters).Count}};
          $methodData | out-chart -charttype bar -groupaxis Name -dataaxis ParameterCount

new-item -name "Mount This Project" -type button -value {
  $selectedProjects | select -first 1 | %{
    pushd "projects:/$($_.Name)";
    write-prompt;
    invoke-item 'windows:/studio shell';
  }
}
